import axios from "axios";
import Router from "next/router";

export const loginUser = async (email, password) => {
  const { data } = await axios.post("/api/login", { email, password });
  if (typeof window !== "undefined") {
    window[WINDOW_USER_SCRIPT_VARIABLE] = data || {};
  }
};

export const logOut = async () => {
  if (typeof window !== "undefined") {
    window[WINDOW_USER_SCRIPT_VARIABLE] = {};
  }

  await axios.post("/api/logout");
  Router.push("/login");
};

export const getServerSideToken = (req) => {
  const { signedCookies = {} } = req;
  if (!signedCookies) return {};
  else if (!signedCookies.token) return {};
  return { user: signedCookies.token };
};
export const getClientSideToken = () => {
  if (typeof window !== "undefined") {
    const user = window[WINDOW_USER_SCRIPT_VARIABLE] || {};
    return { user };
  }
  return { user: {} };
};

export const authInitialProps = (isProtectedRoute) => ({ req, res }) => {
  const auth = req ? getServerSideToken(req) : getClientSideToken();
  const currentPath = req ? req.url : window.location.pathname;
  const { user } = auth;
  const isAnonymous = !user || user.type !== "authenticated";
  if (isProtectedRoute && isAnonymous && currentPath != "/login") {
    return redirectUser();
  }
  return { auth };
};

const WINDOW_USER_SCRIPT_VARIABLE = "__USER__";

export const getUserScript = (user) => {
  return `${WINDOW_USER_SCRIPT_VARIABLE} = ${JSON.stringify(user)};`;
};

export const getUserProfile = async () => {
  const { data } = await axios.get("/api/profile", { withCredentials: true });
  return data;
};
